	(select isnull(sum(samples_count), 0)
	from @samples s
	where not (s.is_russia_production = 0 and isnull(s.is_russia_document, 0) = 0)),
	(select NULL),
	(select NULL),
	--------------------
	(select NULL),(select NULL),(select NULL),(select NULL),(select NULL),(select NULL),(select NULL),(select NULL),
