			   ),0),
			   '1:0,'+convert(varchar(10),@cnt)+';0:0,'+convert(varchar(10),@cnt)+';','LTR'

		open unit_import
		fetch next from unit_import into @unit
		WHILE @@FETCH_STATUS = 0
		BEGIN
			insert @result_table
				select '', '', isnull(@unit,'<нет ед>'), 
