	if @doc_id > 0 
		set @di = @doc_id
	else
		set @di = (SELECT MAX(doc_id) AS doc_id
			FROM ATF_Docs_Agreements() da inner join
				 ATF_R_clients() rc on da.client_guid = rc.client_guid
			WHERE rc.client_guid = @client_guid)
				
